-- ----------------------------
-- Table structure for `exercisedetail`
-- ----------------------------
DROP TABLE IF EXISTS `exercisedetail`;
CREATE TABLE `exercisedetail` (
  `id` int(20) NOT NULL AUTO_INCREMENT,
  `muscle_type_id` int(20) DEFAULT NULL,
  `exercise_detail_name` varchar(200) DEFAULT NULL,
  `exercise_detail_image` varchar(400) DEFAULT NULL,
  `exercise_detail_info` text,
  `hiep` int(11) DEFAULT NULL,
  `timeBreak` int(11) DEFAULT NULL,
  `timeExercise` int(11) DEFAULT NULL,
  `videoID` varchar(100) DEFAULT NULL,
  `isDeleted` bit(1) DEFAULT NULL,
  `createdDate` datetime DEFAULT NULL,
  `createdBy` varchar(20) DEFAULT NULL,
  `modifiedDate` datetime DEFAULT NULL,
  `modifiedBy` varchar(20) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=17 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of exercisedetail
-- ----------------------------
INSERT INTO exercisedetail VALUES ('5', '6', 'Khởi động ver1', 'https://firebasestorage.googleapis.com/v0/b/fitneasy-d43a0.appspot.com/o/ANHGYM%2FBAITAPNGUC%2Fkhoi-dong-truoc-khi-tap-gym.png?alt=media&token=b763a3f1-c3b7-4cbb-811d-d7f3a47f2e33', 'Điều quan trọng nhất trong danh sách lịch tập gym 6 buổi của chúng ta là phải khởi động trước trong tất cả các buổi tập. Nếu bạn đã đọc bài viết những điều nên làm trước và sau khi tập bên trên thì chắc biết là khởi động quan trọng như thế nào rồi. Các bài khởi động như xoay co tay, chân, lưng, hông và chạy tại chỗ từ 2-5 phút. Có thể khỏi động thêm với tạ tay để làm nóng các cơ.', '3', '30', '30', null, null, null, null, null, null);
INSERT INTO exercisedetail VALUES ('6', '7', 'Barbell Bench Press - Medium Grip Bài tập cơ ngực', 'https://firebasestorage.googleapis.com/v0/b/fitneasy-d43a0.appspot.com/o/ANHGYM%2FBAITAPNGUC%2FBarbell-Bench-Press.gif?alt=media&token=d5ed44ee-57a4-4249-b177-04cb5130b23a', '1. Chuẩn bị 1 thanh tạ vừa sức với khả năng của m&igrave;nh, c&aacute;c bạn c&oacute; thể biết nhờ v&agrave;o <strong>b&agrave;i t&iacute;nh 1RM</strong> b&ecirc;n tr&ecirc;n. Nằm ngửa v&agrave;o ghế, b&agrave;n ch&acirc;n nằm tr&ecirc;n s&agrave;n, tay cầm thanh tạ, rộng hơn vai v&agrave; nhấc thanh tạ ra khỏi gi&aacute; tập v&agrave; thở ra.<br />2. H&iacute;t v&agrave;o sau đ&oacute; từ từ hạ xuống tr&ecirc;n ngực c&aacute;ch khoảng 2 cm.<br />3. Đẩy tạ l&ecirc;n v&agrave; đồng thời thở ra.</p> <p>Thực hiện trong 2 hiệp, mỗi hiệp từ 10-15 lần', '3', '30', '30', null, null, null, null, null, null);
INSERT INTO exercisedetail VALUES ('7', '8', 'Dumbbell Bench Press – Đẩy tạ tay – Tập ngực giữa', 'https://firebasestorage.googleapis.com/v0/b/fitneasy-d43a0.appspot.com/o/ANHGYM%2FBAITAPNGUC%2FDumbbell-Bench-Press-bai-tap-nguc.gif?alt=media&token=11c37f23-7b5c-42a9-ab4f-7b9939f22c8f', '1. Nằm tr&ecirc;n ghế, 2 tạ đặt l&ecirc;n tr&ecirc;n đ&ugrave;i. D&ugrave;ng lực ở đ&ugrave;i n&acirc;ng tạ l&ecirc;n v&agrave; đưa sang 2 b&ecirc;n ngực, c&aacute;nh tay s&aacute;t th&acirc;n người v&agrave; kh&ocirc;ng kh&oacute;a khớp c&ugrave;i chỏ nh&eacute;. H&iacute;t v&agrave;o s&acirc;u.<br />2. Thở ra đồng thời đẩy tạ l&ecirc;n cao, ch&uacute; &yacute; đẩy dứt kho&aacute;t v&agrave; thẳng tay (thẳng tay trong thể h&igrave;nh kh&ocirc;ng c&oacute; kh&oacute;a khớp c&aacute;c bạn nha).<br />Thực hiện động t&aacute;c trong 2 hiệp từ 10-12 lần.', '3', '30', '30', null, null, null, null, null, null);
INSERT INTO exercisedetail VALUES ('8', '9', 'Barbell Incline Bench Press – Đẩy tạ trên ghế nghiêng – Tập ngực trên', 'https://firebasestorage.googleapis.com/v0/b/fitneasy-d43a0.appspot.com/o/ANHGYM%2FBAITAPNGUC%2Fbai-tap-nguc-Barbell-Incline-Bench-Press.gif?alt=media&token=c70d91a0-47e1-4b4c-8689-32fbc73246fa', '<div class=\\\"separator\\\"><strong>TH&Ocirc;NG TIN B&Agrave;I TẬP:</strong></div>\\n<div class=\\\"separator\\\"> </div>\\n<div class=\\\"separator\\\"><strong>Kiểu tập: </strong>                       Strength - Sức mạnh </div>\\n<div class=\\\"separator\\\"><strong>Nh&oacute;m cơ ch&iacute;nh:     </strong>        Chest - Cơ ngực</div>\\n<div class=\\\"separator\\\"><strong>Nh&oacute;m cơ tham gia:  </strong>      Shoulders - Cơ vai , Triceps - Cơ tay sau</div>\\n<div class=\\\"separator\\\"><strong>Loại t&aacute;c động:    </strong>            Compound </div>\\n<div class=\\\"separator\\\"><strong>Dụng cụ tập:     </strong>             Barbell - Thanh tạ</div>\\n<div class=\\\"separator\\\">Cấp độ:                           Basic - Cơ bản</div>\\n<div class=\\\"separator\\\"> </div>\\n<div class=\\\"separator\\\"><strong>HƯỚNG DẪN TẬP:</strong></div>\\n<div class=\\\"separator\\\"> </div>\\n<p><span id=\\\"result_box\\\" class=\\\"\\\" lang=\\\"vi\\\"><span class=\\\"hps\\\">1. Nằm l&ecirc;n ghế phẳng, 2 tay nắm v&agrave;o thanh tạ với độ rộng sao cho khi xuống tạ, c&aacute;nh tay song song với mặt đất th&igrave; c&aacute;nh tay tạo với cẳng tay 1 g&oacute;c 90 độ. N&acirc;ng thanh tạ ra khỏi gi&aacute;</span></span>. Giữ tay thẳng. Đ&acirc;y l&agrave; tư thế bắt đầu b&agrave;i tập.<strong><br /></strong></p>\\n<div class=\\\"separator\\\"><span id=\\\"result_box\\\" class=\\\"\\\" lang=\\\"vi\\\">3. Từ từ hạ tạ xuống ph&iacute;a dưới cho đến khi gần chạm v&agrave;o ngực giữa của bạn. H&iacute;t v&agrave;o trong suốt qu&aacute; tr&igrave;nh hạ tạ.     </span></div>\\n<div class=\\\"separator\\\"> </div>\\n<p><span id=\\\"result_box\\\" class=\\\"\\\" lang=\\\"vi\\\"><span class=\\\"hps\\\">4. </span></span>D&ugrave;ng cơ ngực đẩy thanh tạ l&ecirc;n. Thở ra. H&atilde;y nhớ, tập trung tinh thần, sự ch&uacute; &yacute; v&agrave;o cơ ngực, kh&ocirc;ng sử dụng qu&aacute; nhiều tay sau v&agrave; vai trong động t&aacute;c đẩy (Xem lại kỹ thuật ở ảnh hướng dẫn tr&ecirc;n tập lại cho ch&iacute;nh x&aacute;c)<br /><span id=\\\"result_box\\\" class=\\\"\\\" lang=\\\"vi\\\">  <br />5. Lặp lại chuyển động cho reps sau.</span><strong><br /><br /></strong></p>\\n<div class=\\\"separator\\\"><strong>TIPS:</strong></div>\\n<div class=\\\"separator\\\"> </div>\\n<div class=\\\"separator\\\">1. Để đảm bảo b&agrave;i đẩy ngực kh&ocirc;ng ăn qu&aacute; nhiều v&agrave;o vai v&agrave; tay sau. bạn h&atilde;y thực hiện tập dượt động t&aacute;c trước khi bắt đầu đẩy. Lưu ảnh v&agrave;o trong điện thoại, ngồi trước gương, xoay người song song với gương. Tạo tư thế đ&uacute;ng v&agrave; sai, thử cử động xem sự kh&aacute;c nhau giữa 2 tư thế để c&oacute; cảm nhận tốt nhất.</div>\\n<div class=\\\"separator\\\"> </div>\\n<div class=\\\"separator\\\">2. Giữ vững sự tập trung tinh thần, kh&ocirc;ng để tay run, tạ lệch hoặc l&uacute;c lắc.</div>\\n<div class=\\\"separator\\\"> </div>\\n<div class=\\\"separator\\\">3. Hạ tạ c&oacute; điều khiển, kh&ocirc;ng để tạ rơi tự do.</div>\\n<div class=\\\"headline headline-md\\\">\\n<h2>1. Tổng Quan Incline Barbell Bench Press Đẩy Ngực Ghế Dốc</h2>\\n</div>\\n<p>- Đ&acirc;y l&agrave; 1 trong những động t&aacute;c free weight tốt nhất, hiệu quả nhất để t&aacute;c động đến to&agrave;n bộ cơ ngực m&agrave; cụ thể l&agrave; v&ugrave;ng cơ ngực ph&iacute;a tr&ecirc;n sẽ được t&aacute;c động nhiều</p>\\n<p>- C&aacute;c cơ tham gia hoạt động: Cơ ngực (chests), cơ tay sau (cơ tam đầu triceps), cơ vai (shoulders).</p>\\n<p>- Bạn c&oacute; thể d&ugrave;ng động t&aacute;c bulking x&acirc;y dựng k&iacute;ch thước cơ ngực, động t&aacute;c n&agrave;y l&agrave; động t&aacute;c kết hợp nhiều nh&oacute;m cơ (ngực - chest, tay sau - triceps, vai - shoulders, m&ocirc;ng - glutes, đ&ugrave;i - quads hamstring) n&ecirc;n sẽ cho ph&eacute;p bạn tập được khối lượng tạ rất lớn.</p>\\n<p>- <strong>Biến thể: </strong>B&agrave;i tập n&agrave;y c&oacute; 1 c&aacute;ch tập kh&aacute;c l&agrave; sử dụng ghế adjustable bench kết hợp với m&aacute;y smith m&agrave; t&ocirc;i sẽ giới thiệu đến c&aacute;c bạn trong 1 b&agrave;i viết kh&aacute;c.</p>\\n<div class=\\\"headline headline-md\\\">\\n<h2>2. C&aacute;ch Tập Incline Barbell Bench Press Đẩy Ngực Ghế Dốc</h2>\\n</div>\\n<p>- Đầu ti&ecirc;n bạn phải c&oacute; được 1 c&aacute;i ghế ngang Olympic Incline Bench Press v&agrave; 1 c&acirc;y barbell chuẩn Olympic d&agrave;i 2m2 nặng 44lbs (tức l&agrave; 20.4kg). Một số ph&ograve;ng thể h&igrave;nh dụng cụ kh&ocirc;ng chuẩn th&igrave; c&aacute;c bạn sẽ kh&ocirc;ng đạt được đến những con số n&agrave;y. C&acirc;y barbell nhỏ qu&aacute; sẽ l&agrave;m cho động t&aacute;c kh&ocirc;ng ổn định tạ hơi lắc v&agrave; bạn sẽ đau tay hơn.</p>\\n<p>- Điều chỉnh c&acirc;y đ&ograve;n barbell về vị tr&iacute; bạn dễ lấy ra nhất khi đẩy ngực, lắp tạ v&agrave;o v&agrave; kh&oacute;a chặt tạ 2 b&ecirc;n đầu c&acirc;y để tr&aacute;nh tạ rơi ra trong qu&aacute; tr&igrave;nh thực hiện động t&aacute;c, đ&acirc;y l&agrave; bước quan trọng</p>\\n<p>- <strong>Tập nặng bao nhi&ecirc;u?</strong> Kh&ocirc;ng cần đẩy qu&aacute; nhiều, chỉ cần từ 6-10 reps cho 1 mức tạ nặng l&agrave; bạn c&oacute; thể x&acirc;y dựng rất tốt cơ ngực ở b&agrave;i tập n&agrave;y</p>\\n<p>-<strong>Chuẩn bị trang thiết bị hỗ trợ</strong> Ngo&agrave;i bao tay ra bạn cần phải c&oacute; th&ecirc;m d&acirc;y quấn cổ tay, l&uacute;c n&agrave;y l&agrave; bạn đeo d&acirc;y quấn cổ tay v&agrave;o để bắt đầu b&agrave;i tập ngay lập tức</p>\\n<p>- <strong>Bước chuẩn bị</strong> đ&acirc;y l&agrave; 1 bước rất quan trọng v&agrave; đầy kh&oacute; khăn, bạn cần nằm thật ổn định tr&ecirc;n ghế, vị tr&iacute; cằm thẳng với c&acirc;y đ&ograve;n l&agrave; được, cầm đ&ograve;n tạ rộng hơn vai 1 ch&uacute;t (khoảng 1.5 lần vai trở lại), 5 ng&oacute;n tay nằm c&ugrave;ng 1 ph&iacute;a, h&iacute;t thật s&acirc;u sau đ&oacute; gồng người (nhất l&agrave; cơ ngực), ch&acirc;n đạp mạnh tr&ecirc;n s&agrave;n, ưỡn ngực đẩy đ&ograve;n tạ ra khỏi gi&aacute; đỡ thẳng tay nhưng kh&ocirc;ng kh&oacute;a khớp c&ugrave;i chỏ (xem th&ecirc;m trong video) sau đ&oacute; thở mạnh ra, đ&oacute; l&agrave; xong bước bắt đầu.</p>\\n<p>- <strong>Kỹ thuật thực hiện 1 reps</strong> Sau khi thực hiện xong bước tr&ecirc;n th&igrave; đ&ograve;n tạ nằm ở vị tr&iacute; cao nhất, b&acirc;y giờ bạn hạ tạ xuống chạm nhẹ v&agrave;o ngực (tr&ecirc;n đầu ngực 1 ch&uacute;t x&iacute;u), khi hạ tạ xuống bạn nhớ gồng cơ ngực v&agrave; cơ tay sau để kh&aacute;ng lại trọng lực tạ l&agrave;m cho đ&ograve;ng tạ kh&ocirc;ng rơi qu&aacute; nhanh (xem th&ecirc;m video) đồng thời h&iacute;t s&acirc;u (vừa hạ vừa h&iacute;t). Khi đ&ograve;n tạ chạm nhẹ ngực th&igrave; bạn bắt đầu tập trung điểu khiển cơ ngực của bạn &eacute;p lại thật mạnh đẩy đ&ograve;n tạ l&ecirc;n x&eacute;o về ph&iacute;a đầu khoảng 5cm đồng thời thở mạnh ra. Xin n&oacute;i th&ecirc;m chỗ n&agrave;y: khi đẩy l&ecirc;n mặc d&ugrave; bạn gồng ngực nhưng cơ tay sau v&agrave; vai vẫn được gồng để hỗ trợ, đẩy tạ l&ecirc;n vị tr&iacute; cao nhất th&igrave; đ&ograve;n tạ sẽ nằm ở vị tr&iacute; thẳng với ngực ph&iacute;a tr&ecirc;n gần cổ. <strong>Ch&uacute; &yacute;: </strong><em>Thời gian xuống tạ &iacute;t nhất l&agrave; gấp đ&ocirc;i thời gian l&ecirc;n tạ</em></p>\\n<p>- <strong>Tr&aacute;nh kh&oacute;a khớp</strong> Thật nguy hiểm khi bạn đẩy ngực m&agrave; bị kh&oacute;a khớp, bạn n&ecirc;n triệt để tr&aacute;nh lỗi n&agrave;y, nếu bạn chưa biết thế n&agrave;o l&agrave; kh&oacute;a khớp th&igrave; bạn vui l&ograve;ng đọc lại b&agrave;i viết m&ocirc; tả về kh&oacute;a khớp ở những d&ograve;ng đầu ti&ecirc;n của b&agrave;i viết n&agrave;y.</p>\\n<p>- Sau khi thực hiện xong số reps cần thiết th&igrave; bạn trả thanh đ&ograve;n barbell về gi&aacute; đỡ như cũ, sau đ&oacute; ngồi dậy th&aacute;o d&acirc;y quấn cổ tay ra cho m&aacute;y huyết lưu th&ocirc;ng phục vụ cho việc phục hồi cơ, bạn h&atilde;y đi lại v&agrave; nghỉ ngơi 2-3p v&agrave; nhớ đừng qu&ecirc;n uống nước hoặc thực phẩm bổ sung intra workout l&uacute;c n&agrave;y nh&eacute;.</p>\\n<p>- <strong>Cảnh b&aacute;o: </strong>Ở b&agrave;i tập n&agrave;y bạn kh&ocirc;ng n&ecirc;n cầm c&acirc;y qu&aacute; rộng như vậy &aacute;p lực cơ ngực sẽ kh&ocirc;ng nhiều v&agrave; quỹ đạo chuyển động của cơ ngực cũng kh&ocirc;ng d&agrave;i bằng c&aacute;c bạn tập vừa mặc d&ugrave; bạn sẽ c&oacute; cảm gi&aacute;c cơ ngực căng nhưng sẽ rất nguy hiểm v&agrave; đầy kh&oacute; khăn với khối lượng tạ nặng. Một sai lầm kh&aacute;c l&agrave; c&aacute;c bạn nghĩ rằng ghế c&oacute; độ dốc c&agrave;ng cao sẽ c&agrave;ng tốt cho cơ ngực tr&ecirc;n, điều n&agrave;y thật sự l&agrave; 1 sai lầm tai hại khi ghế c&agrave;ng cao th&igrave; cơ tham gia ch&iacute;nh sẽ l&agrave; cơ vai, độ dốc l&yacute; tưởng cho sự t&aacute;c động tối đa v&ugrave;ng ngực ph&iacute;a tr&ecirc;n l&agrave; 30 độ.</p>\\n<div class=\\\"headline headline-md\\\">\\n<h2>3. Khi N&agrave;o Tập Incline Barbell Bench Press Đẩy Ngực Tr&ecirc;n</h2>\\n<p>Chắc chắn ngực tr&ecirc;n phải được tập trong 1 buổi tập ngực của bạn, do đ&oacute; b&agrave;i tập n&agrave;y kh&ocirc;ng thể bỏ qua mặc d&ugrave; c&oacute; 1 đối thủ cạnh tranh rất quyết liệt với b&agrave;i tập n&agrave;y l&agrave; b&agrave;i Inlince Dumbbell Bench Press. Bạn cũng c&oacute; thể tập cả 2 b&agrave;i n&agrave;y trong 1 buổi tập (v&iacute; dụ mỗi b&agrave;i 2 sets) hoặc chọn 1 trong 2 để tập, cả 2 đều rất tốt để ph&aacute;t triển v&ugrave;ng ngực ph&iacute;a tr&ecirc;n Chests, cơ tam đầu bắp tay sau triceps v&agrave; cơ vai shoulders.</p>\\n</div>\\n<p><strong> </strong></p>', '3', '30', '30', null, null, null, null, null, null);
INSERT INTO exercisedetail VALUES ('9', '10', 'Incline Dumbbell Press – Đẩy tạ tay trên ghế nghiêng – Tập ngực trên', 'https://firebasestorage.googleapis.com/v0/b/fitneasy-d43a0.appspot.com/o/ANHGYM%2FBAITAPNGUC%2Fbai-tap-nguc-Dumbbell-Incline-Bench-Press.jpg?alt=media&token=dd169f95-e5f0-4d81-9932-47c37fb06c1f', 'Kỹ thuật tập tương tự như b&agrave;i s&ocirc; 3 b&ecirc;n tr&ecirc;n kh&ocirc;ng c&oacute; g&igrave; kh&aacute;c ngo&agrave;i việc điều chỉnh độ nghi&ecirc;ng của ghế tập c&aacute;c bạn nha.</p> <p>B&agrave;i 6: Barbell Decline Bench Press &ndash; Đẩy tạ với ghế nghi&ecirc;ng xuống &ndash; Tập ngực dưới<br />Kĩ thuật giống như b&agrave;i số 4 nhưng thay v&igrave; chỉnh ghế l&ecirc;n cao th&igrave; ch&uacute;ng ta sẽ chỉnh ghế xuống thấp hơn.', '3', '30', '30', null, null, null, null, null, null);
INSERT INTO exercisedetail VALUES ('10', '11', 'Barbell Decline Bench Press – Đẩy tạ với ghế nghiêng xuống – Tập ngực dưới', 'https://firebasestorage.googleapis.com/v0/b/fitneasy-d43a0.appspot.com/o/ANHGYM%2FBAITAPNGUC%2Fbai-tap-nguc-Dumbbell-Incline-Bench-Press.jpg?alt=media&token=dd169f95-e5f0-4d81-9932-47c37fb06c1f', '<div class=\\\"separator\\\">\\n<div class=\\\"headline headline-md\\\">\\n<h2><span style=\\\"color: #3366ff;\\\">1. Decline Barbell Bench Press Tập Ngực Dưới To Khỏe</span></h2>\\n</div>\\n<p>- Đ&acirc;y l&agrave; 1 b&agrave;i tập BULKING kết hợp (compound) ph&aacute;t triển cơ ngực chests d&agrave;nh cho những bạn sau khi ho&agrave;n th&agrave;nh chương tr&igrave;nh tập thể h&igrave;nh cho người mới</p>\\n<p>- C&aacute;c cơ tham gia hoạt động: Cơ ngực chest, cơ tam đầu bắp tay sau triceps, cơ vai shoulders.</p>\\n<p>- B&agrave;i tập n&agrave;y chủ yếu ph&aacute;t triển sức mạnh, k&iacute;ch thước c&aacute;c nh&oacute;m cơ tham gia chủ yếu l&agrave; cơ ngực.</p>\\n<div class=\\\"alert alert-success\\\">\\n<h4>Khi N&agrave;o Được Tập B&agrave;i N&agrave;y?</h4>\\nSau khi ho&agrave;n th&agrave;nh chương tr&igrave;nh tập luyện cơ bản m&agrave; t&ocirc;i đề cập ph&iacute;a tr&ecirc;n th&igrave; bạn đ&atilde; c&oacute; thể tập b&agrave;i n&agrave;y, nếu bạn mới tạp thể h&igrave;nh v&agrave; bắt tay tập b&agrave;i n&agrave;y liền th&igrave; bạn sẽ kh&oacute; th&iacute;ch ứng được bởi kỹ thuật động t&aacute;c của bạn l&uacute;c đ&oacute; chưa vững, tay run, sức yếu rất dễ bị chấn thương khi tập luyện (bạn cần luyện c&ocirc;ng với m&aacute;y selectorize trước, nếu ph&ograve;ng bạn kh&ocirc;ng c&oacute; m&aacute;y selectorized cho bạn tập ngực th&igrave; kh&ocirc;ng c&ograve;n c&aacute;ch n&agrave;o kh&aacute;c bạn tập nhẹ th&ocirc;i bằng c&acirc;y kh&ocirc;ng hoặc thậm ch&iacute; bằng tay kh&ocirc;ng), b&agrave;i n&agrave;y sẽ được nằm tư thế dốc xuống đ&ocirc;i khi sẽ l&agrave;m cho bạn ch&oacute;ng mặt. Một vấn đề kh&aacute;c l&agrave; cần phải tập cho ngực to l&ecirc;n rồi mới qua chơi b&agrave;i n&agrave;y, điều n&agrave;y x&eacute;t về mặt kỹ thuật v&agrave; thời gian l&agrave; hợp l&yacute; nhưng nếu x&eacute;t về th&agrave;nh t&iacute;ch th&igrave; kh&ocirc;ng đ&uacute;ng 1 t&iacute; n&agrave;o về mặt logic, chẳng lẽ bạn phải mất nhiều thời gian như vậy để c&oacute; 1 bộ ngực ho&agrave;n chỉnh sao? Bạn n&ecirc;n nhớ trong thể h&igrave;nh ph&aacute;t triển c&acirc;n đối h&agrave;i h&ograve;a giữa c&aacute;c bộ phận tr&ecirc;n cơ thể l&agrave; điều quan trọng nhất, bạn kh&ocirc;ng n&ecirc;n tập c&aacute;c nh&oacute;m cơ 1 c&aacute;ch lệch như vậy. Đ&acirc;y l&agrave; 1 lời khuy&ecirc;n ch&acirc;n th&agrave;nh d&agrave;nh cho c&aacute;c bạn.</div>\\n<p>-Cảnh b&aacute;o: Ghế tập ph&ugrave; hợp cho b&agrave;i n&agrave;y l&agrave; những ghế tập decline c&oacute; độ dốc 30 độ, những ghế c&oacute; độ dốc nhiều hơn tập luyện sẽ nguy hiểm hơn trong khi kết quả lại giảm s&uacute;t nhiều, c&aacute;c bạn n&ecirc;n lưu &yacute; vấn đề n&agrave;y. Nếu ph&ograve;ng tập của bạn kh&ocirc;ng c&oacute; ghế chuẩn để bạn tập th&igrave; bạn vui l&ograve;ng bỏ b&agrave;i n&agrave;y thay bằng b&agrave;i Dips Chests Version sẽ tốt hơn, c&ograve;n nếu ph&ograve;ng của bạn kh&ocirc;ng c&oacute; trang bị tốt để dips lu&ocirc;n th&igrave; bạn n&ecirc;n nghĩ đến chuyện đổi ph&ograve;ng tập kh&aacute;c, bạn sẽ rất kh&oacute; tiến xa với 1 m&ocirc;i trường tập luyện như thế.</p>\\n<div class=\\\"headline headline-md\\\">\\n<h2>2. Decline Barbell Bench Press Tập Ngực Dưới Đ&uacute;ng Kỹ Thuật Thể H&igrave;nh</h2>\\n</div>\\n<p>- Đầu ti&ecirc;n bạn phải c&oacute; được 1 ghế tập decline bench press v&agrave; 1 c&acirc;y barbell chuẩn Olympic d&agrave;i 2m2 nặng 44lbs (tức l&agrave; 20.4kg). Một số ph&ograve;ng thể h&igrave;nh dụng cụ kh&ocirc;ng chuẩn th&igrave; c&aacute;c bạn sẽ kh&ocirc;ng đạt được đến những con số n&agrave;y. C&acirc;y barbell nhỏ qu&aacute; sẽ l&agrave;m cho dộng t&aacute;c kh&ocirc;ng ổn định tạ hơi lắc v&agrave; bạn sẽ đau tay hơn..</p>\\n<p>- Bạn chỉnh thanh barbell về vị tr&iacute; ph&uacute; hợp nhất để bạn c&oacute; thể lấy ra khỏi gi&aacute; đỡ khi tập luyện, th&ecirc;m tạ vừa sức, chốt 2 đầu c&acirc;y barbell lại cho tạ khỏi di chuyển khi tập luyện, chỉnh chiều d&agrave;i ghế ph&ugrave; hợp cới chiều cao của bạn sao cho khi nằm xuống mắt của bạn tr&ecirc;n c&acirc;y d&ograve;n tạ 1 ch&uacute;t x&iacute;u.</p>\\n<p>- <strong>Mức tạ:</strong> đ&acirc;y l&agrave; b&agrave;i bulking kết hợp n&ecirc;n bạn cố gắng thực hiện ở mức tạ nặng từ 6-10 reps 1 set (MaxOT th&igrave; 4-6 reps).</p>\\n<p>- <strong>Chuẩn bị</strong> Kỹ thuật lấy tạ ra giống hệt trong b&agrave;i tuy nhi&ecirc;n l&uacute;c n&agrave;y ch&acirc;n bạn kh&ocirc;ng c&ograve;n chạm đất nữa m&agrave; th&ocirc;i <a href=\\\"http://www.thehinhonline.com.vn/baiviet/chitiet/day-nguc-bang-ta-don-tren-ghe-phang\\\" target=\\\"_blank\\\">Flat Barbell Bench Press</a>.</p>\\n<p>- <strong>Kỹ thuật thực h&agrave;nh 1 rep</strong> Ở vị tr&iacute; bắt đầu bạn h&iacute;t s&acirc;u, gồng cơ ngực đồng thời hạ tạ xuống với tốc độ vừa phải (tạ kh&ocirc;ng hạ theo đường thẳng m&agrave; x&eacute;o về ph&iacute;a bụng khoảng 5cm), đến khi đ&ograve;n tạ chạm nhẹ v&agrave;o ngực dưới (vị tr&iacute; đầu v&uacute; của bạn) l&uacute;c n&agrave;y cơ đ&atilde; căng tối đa bạn giữ lại 1s, sau đ&oacute; gồng cơ ngực cơ vai shoulder v&agrave; cơ tam đầu bắp tay sau triceps đẩy tạ mạnh ngược l&ecirc;n vị tr&iacute; bạn đầu rồi thở ra (kh&ocirc;ng đẩy thẳng l&ecirc;n m&agrave; vẫn x&eacute;o 5cm về ph&iacute;a đầu), giữ lại tư thế đ&oacute; 1-2s. Vậy l&agrave; xong 1 reps.</p>\\n<p>- Sau khi thực hiện xong số reps cần thiết th&igrave; bạn h&atilde;y đi ra khỏi vị tr&iacute; t&acirc;p v&agrave; nghỉ ngơi 2-3p v&agrave; nhớ đừng qu&ecirc;n uống nước hoặc thực phẩm bổ sung intra workout l&uacute;c n&agrave;y nh&eacute;.</p>\\n<p>- <strong>Ch&uacute; &yacute;:</strong> Kh&ocirc;ng n&ecirc;n cầm tay qu&aacute; rộng ở động t&aacute;c n&agrave;y sẽ nguy hiểm v&agrave; kh&ocirc;ng mang lại th&agrave;nh t&iacute;ch tốt nhất cho bạn, bạn cũng n&ecirc;n ch&uacute; &yacute; kh&ocirc;ng kh&oacute;a khớp c&ugrave;i chỏ khi tập luyện (nếu bạn chưa biết thế n&agrave;o l&agrave; kh&oacute;a khớp th&igrave; h&atilde;y xem link đầu b&agrave;i viết), B&agrave;i tập n&agrave;y khi xuống tạ kh&ocirc;ng xuống vị tr&iacute; ngực tr&ecirc;n gần cổ, đ&acirc;y l&agrave; con đường ngắn nhất để bị tạ đ&egrave; v&agrave; chấn thương vai. Nếu bạn tập đ&uacute;ng th&igrave; b&agrave;i tập n&agrave;y cho ph&eacute;p bạn tập khối lượng tạ ngang bằng với b&agrave;i Flat Barbell Bench Press.</p>\\n<div class=\\\"headline headline-md\\\">\\n<h2>3. To Khỏe Ngực Dưới Với Decline Barbell Bench Press</h2>\\n</div>\\n<div class=\\\"alert alert-success\\\">Đ&acirc;y l&agrave; 1 b&agrave;i tập rất tốt để ph&aacute;t triển v&ugrave;ng ngực ph&iacute;a dưới nếu thiết bị bạn tập luyện đ&uacute;ng chuẩn h&atilde;y tập trong mọi gi&aacute;o &aacute;n BULKING của bạn, nhưng cũng l&agrave; con đường gần nhất d&iacute;nh chấn thương nếu thiết bị bị sai. C&oacute; thể n&oacute;i đ&acirc;y l&agrave; 1 con dao 2 lưỡi đầy may rủi v&agrave; đ&atilde; c&oacute; rất nhiều kinh nghiệm \\\"xương m&aacute;u\\\" ở b&agrave;i tập n&agrave;y. Bạn sẽ ổn nếu bạn ch&uacute; &yacute; những c&ugrave;m từ cơ bản m&agrave; t&ocirc;i c&ocirc; đọng lại như sau: ghế kh&ocirc;ng qu&aacute; dốc, cầm tay kh&ocirc;ng qu&aacute; rộng, xuống tạ gần bụng, kh&ocirc;ng kh&oacute;a khớp c&ugrave;i chỏ. Xin ch&uacute;c c&aacute;c bạn th&agrave;nh c&ocirc;ng với b&agrave;i tập n&agrave;y.</div>\\n</div>', '3', '30', '30', null, null, null, null, null, null);
INSERT INTO exercisedetail VALUES ('11', '6', 'Dumbbell Flyes – Tập mở rộng ngực với tạ tay ', 'https://firebasestorage.googleapis.com/v0/b/fitneasy-d43a0.appspot.com/o/ANHGYM%2FBAITAPNGUC%2Fbai-tap-nguc-Dumbbell-Flyes.gif?alt=media&token=5a9ccecb-5bc3-441f-8640-a613f9d98231', '<div class=\\\"separator\\\"><strong>TH&Ocirc;NG TIN B&Agrave;I TẬP:</strong></div>\\n<div class=\\\"separator\\\"> </div>\\n<div class=\\\"separator\\\"><strong>Kiểu tập: </strong>                       Strength - Sức mạnh </div>\\n<div class=\\\"separator\\\"><strong>Nh&oacute;m cơ ch&iacute;nh:     </strong>        Chest - Cơ ngực</div>\\n<div class=\\\"separator\\\"><strong>Nh&oacute;m cơ tham gia:  </strong>      Kh&ocirc;ng</div>\\n<div class=\\\"separator\\\"><strong>Loại t&aacute;c động:    </strong>            Isolation</div>\\n<div class=\\\"separator\\\"><strong>Dụng cụ tập:     </strong>             Dumbbell - Tạ đơn</div>\\n<div class=\\\"separator\\\">Cấp độ:                           Basic - Cơ bản</div>\\n<div class=\\\"separator\\\"> </div>\\n<div class=\\\"separator\\\"><strong>HƯỚNG DẪN TẬP:</strong></div>\\n<p><span id=\\\"result_box\\\" class=\\\"\\\" lang=\\\"vi\\\"><span class=\\\"hps\\\">1. Ngồi l&ecirc;n ghế băng với 2 quả tạ đặt tr&ecirc;n đ&ugrave;i, l&ograve;ng b&agrave;n tay hướng v&agrave;o nhau.</span></span> Nằm xuống đồng thời đẩy đ&ugrave;i của bạn l&ecirc;n đưa 2 quả tạ l&ecirc;n. N&acirc;ng 2 quả tạ l&ecirc;n ph&iacute;a tr&ecirc;n vai. Để 2 l&ograve;ng b&agrave;n tay hướng v&agrave;o nhau. 2 quả tạ c&aacute;ch nhau 1 đoạn. C&aacute;nh tay hơi cong lại, để bắp tay trước kh&ocirc;ng bị căng.<br /><span id=\\\"result_box\\\" class=\\\"\\\" lang=\\\"vi\\\">  <br />3. Hạ tạ bằng c&aacute;ch mở rộng c&aacute;nh tay theo h&igrave;nh v&ograve;ng cung sang 2 b&ecirc;n. Đồng thời h&iacute;t v&agrave;o trong suốt qu&aacute; tr&igrave;nh hạ tạ. Mở rộng cho đến khi bạn cảm thấy cơ ngực được căng cứng. G&oacute;c của c&aacute;nh tay v&agrave; cẳng tay kh&ocirc;ng thay đổi trong suốt qu&aacute; tr&igrave;nh hạ tạ.<br />     <span class=\\\"hps\\\"> </span></span><br /><span id=\\\"result_box\\\" class=\\\"\\\" lang=\\\"vi\\\"><span class=\\\"hps\\\">4. </span></span>D&ugrave;ng cơ ngực k&eacute;o 2 c&aacute;nh tay trở lại theo h&igrave;nh v&ograve;ng cung như l&uacute;c đi xuống. Thở ra.<br /><span id=\\\"result_box\\\" class=\\\"\\\" lang=\\\"vi\\\">  <br />5. Lặp lại chuyển động cho reps sau.</span></p>\\n<div class=\\\"separator\\\"> </div>\\n<div class=\\\"separator\\\"><strong>TIPS:</strong></div>\\n<div class=\\\"separator\\\"> </div>\\n<div class=\\\"separator\\\"> </div>\\n<div class=\\\"separator\\\">1. Kh&ocirc;ng xuống qu&aacute; s&acirc;u v&igrave; c&oacute; thể ảnh hưởng đến khớp vai của bạn.</div>\\n<div class=\\\"separator\\\">2. Kh&ocirc;ng để 2 tạ chạm v&agrave;o nhau khi ở điểm cao nhất. Tạ chạm nhau sẽ l&agrave;m mất lực căng của cơ ngực.</div>\\n<div class=\\\"separator\\\">3. C&aacute;nh tay phải hơi cong. Điều n&agrave;y tốt cho bắp tay trước v&agrave; vai của bạn. Cũng như để đảm bảo cơ ngực được căng trong suốt b&agrave;i tập.</div>\\n<div class=\\\"separator\\\">4. Hạ tạ c&oacute; điều khiển, kh&ocirc;ng để tạ rơi tự do.</div>\\n<div class=\\\"separator\\\">5. Kh&ocirc;ng để khuỷu tay bị gập xuống ph&iacute;a dưới. Cố gắng để cổ tay, khuỷu tay, vai tạo với nhau th&agrave;nh một mặt phẳng trong suốt qu&aacute; tr&igrave;nh di chuyển của b&agrave;i tập.</div>\\n<div class=\\\"separator\\\"><strong>BIẾN THỂ:</strong></div>\\n<div class=\\\"separator\\\"> </div>\\n<div class=\\\"separator\\\">1. Fly với c&aacute;p.</div>\\n<p>C&aacute;c b&agrave;i tập n&agrave;y chỉ cần chọn 1 trong số đ&oacute;</p>\\n<p>1. Nằm tr&ecirc;n ghế với tạ tr&ecirc;n tay, đặt tạ tr&ecirc;n đ&ugrave;i v&agrave; l&ograve;ng b&agrave;n tay cầm tạ hướng v&agrave;o nhau<br />2. Sử dụng đ&ugrave;i để n&acirc;ng tạ l&ecirc;n v&agrave; giữ n&oacute; tr&ecirc;n ngực v&agrave; thẳng tay (ch&uacute; &yacute; lần nữa: kh&ocirc;ng kh&oacute;a khớp).<br />3. H&iacute;t v&agrave;o, từ từ mở rộng ra hướng xuống s&agrave;n nh&agrave;, với c&ugrave;i chỏ cong nhẹ, mở rộng đến khi cảm thấy cơ ngực căng ra.<br />4. Thở ra v&agrave; trở lại vị tr&iacute; ban đầu.<br />Biến thể của b&agrave;i n&agrave;y l&agrave; bạn c&oacute; thể hướng l&ograve;ng b&agrave;n tay tới trước mặt thay v&igrave; hướng v&agrave;o nhau.</p>\\n<p> </p>\\n<div class=\\\"headline headline-md\\\">\\n<h2>1. Tổng Quan Dumbbell Bench Fly &Eacute;p Ngực Trong</h2>\\n</div>\\n<p>- Đ&acirc;y l&agrave; 1 b&agrave;i tập CUTTING d&agrave;nh cho những bạn sau khi ho&agrave;n th&agrave;nh chương tr&igrave;nh <a href=\\\"http://www.thehinhonline.com.vn/baiviet/chitiet/lich-tap-2-thang-dau-tien\\\" target=\\\"_blank\\\">Thể H&igrave;nh cho người mới tập GYM</a></p>\\n<p>- B&agrave;i tập n&agrave;y kh&ocirc;ng đ&ograve;i hỏi qu&aacute; nhiều về trang thiết bị phức tạp v&agrave; kh&ocirc;ng gian tập luyện cũng vừa phải n&ecirc;n bạn c&oacute; thể tập b&agrave;i tập n&agrave;y ở GYM hay ở nh&agrave; (home gym) đều được.</p>\\n<p>- Mặc d&ugrave; đ&acirc;y l&agrave; 1 động t&aacute;c free weight nhưng lại l&agrave; 1 động t&aacute;c c&ocirc; lập (isolation), hiệu quả cho việc cắt n&eacute;t, săn chắc, tăng sức bền, giảm mỡ v&ugrave;ng cơ ngực.</p>\\n<p>- C&aacute;c cơ tham gia hoạt động: Cơ ngực (chests) to&agrave;n bộ v&ugrave;ng ngực</p>\\n<div class=\\\"headline headline-md\\\">\\n<h2>2. Kỹ Thuật Dumbbell Bench Fly &Eacute;p Ngực Trong</h2>\\n</div>\\n<p>- B&agrave;i tập n&agrave;y c&oacute; y&ecirc;u cầu thiết bị v&agrave; điều chỉnh thiết bị giống với b&agrave;i <a href=\\\"http://www.thehinhonline.com.vn/baiviet/chitiet/day-nguc-bang-ta-don-tren-ghe-phang\\\" target=\\\"_blank\\\">Flat Barbell Bench Press Đẩy Ngực Tạ Đ&ograve;n Ghế Phẳng</a>, bạn c&oacute; thể xem qua b&agrave;i đ&oacute; để biết th&ecirc;m chi tiết.</p>\\n<p>- <strong>Về mức tạ tập ch&iacute;nh thức</strong> Bạn cần 2 quả tạ dumbbell c&oacute; khối lượng l&agrave;m sao cho bạn thực hiện được khoảng 12-15 reps trong 1 set, đ&acirc;y l&agrave; 1 động t&aacute;c c&ocirc; lập do đ&oacute; khối lượng tạ của bạn kh&ocirc;ng thể nặng được, b&agrave;i tập n&agrave;y chủ yếu đ&ograve;i hỏi độ bền cơ ngực</p>\\n<p>-<strong>Kỹ thuật &eacute;p ngực</strong> Ở vị tr&iacute; bắt đầu bạn xoay 2 l&ograve;ng b&agrave;n tay hướng v&agrave;o nhau, c&ugrave;i chỏ cong, h&iacute;t thật s&acirc;u sau đ&oacute; từ từ hạ tạ xuống sao cho cơ ngực căng hết cỡ, l&uacute;c n&agrave;y l&ograve;ng b&agrave;n tay ngửa<strong>Ch&uacute; &yacute;: </strong><em>Ở vị tr&iacute; thấp nhất c&ugrave;i chỏ bạn vẫn phải cong v&agrave; kh&ocirc;ng được cao hơn vai cả 2 hướng dọc v&agrave; ngang để đảm bảo t&iacute;nh an to&agrave;n v&agrave; cơ ngực được căng tối đa</em>. Sau đ&oacute; bạn gồng cơ ngực (cơ vai cũng được gồng nhưng chỉ d&ugrave;ng để giữ tạ th&ocirc;i, kh&ocirc;ng tham gia động t&aacute;c, m&igrave;nh phải c&ocirc; lập cơ vai chỉ d&ugrave;ng 1 cơ ngực để &eacute;p tạ l&ecirc;n) &eacute;p mạnh v&agrave;o l&agrave;m 2 c&aacute;nh tay di chuyển đưa tạ l&ecirc;n vị tr&iacute; cao nhất l&uacute;c đầu l&ograve;ng b&agrave;n tay hướng v&agrave;o nhau nhưng tạ kh&ocirc;ng được chạm v&agrave;o nhau nh&eacute; (gần chạm l&agrave; tốt) đồng thời thở ra. C&aacute;i kh&oacute; nhất của động t&aacute;c ch&iacute;nh nằm ở chỗ n&agrave;y, đa phần c&aacute;c bạn d&ugrave;ng sức mạnh cơ tay v&agrave; vai để &eacute;p tạ l&ecirc;n như vậy cơ ngực chẳng được k&iacute;ch th&iacute;ch bao nhi&ecirc;u hết. Bạn n&ecirc;n nhớ kỹ l&agrave; gồng ngực, tập trung d&ugrave;ng sức cơ ngực &eacute;p mạnh v&agrave;o, khi ngực &eacute;p m&agrave; cơ vai đang bị c&ocirc; lập sẽ k&eacute;o 2 c&aacute;nh tay bạn lại gần nhau m&agrave; c&ugrave;i chỏ bạn cũng đang được cố định lại n&ecirc;n 2 quả dumbbell sẽ được di chuyển l&ecirc;n ph&iacute;a tr&ecirc;n. Đ&oacute; mới l&agrave; vấn đề ch&iacute;nh của động t&aacute;c n&agrave;y, xin đừng nhầm lẫn m&agrave; d&ugrave;ng cơ tay v&agrave; vai nh&eacute;.</p>\\n<p>- Sau khi thực hiện xong số reps cần thiết th&igrave; bạn trở về vị tr&iacute; kết th&uacute;c của b&agrave;i Dumbbell Bench Press v&agrave; xuống tạ về vị tr&iacute; cũ <strong>Ch&uacute; &yacute;: <em>Cho d&ugrave; khối lượng tạ nặng hay nhẹ bạn cũng cần phải ch&uacute; &yacute; c&aacute;ch l&ecirc;n xuống tạ đ&uacute;ng kỹ thuật c&oacute; như vậy bạn mới c&oacute; thể tiến xa hơn tr&ecirc;n bản đồ thể h&igrave;nh</em></strong> sau đ&oacute; bạn đi lại v&agrave; nghỉ ngơi 30s-1p (kh&ocirc;ng nghỉ qu&aacute; l&acirc;u ở những động t&aacute;c c&ocirc; lập) v&agrave; nhớ đừng qu&ecirc;n uống nước hoặc thực phẩm bổ sung intra workout l&uacute;c n&agrave;y nh&eacute;.</p>\\n<div class=\\\"headline headline-md\\\">\\n<h2>3. Khi N&agrave;o Tập Dumbbell Bench Fly &Eacute;p Ngực Trong</h2>\\n</div>\\n<div class=\\\"alert alert-info\\\">Như đ&atilde; đề cập ph&iacute;a tr&ecirc;n, đ&acirc;y l&agrave; 1 động t&aacute;c rất dễ tập v&agrave; rất hữu hiệu để cắt n&eacute;t cơ ngực, bạn c&oacute; thể tập động t&aacute;c n&agrave;y trong bất k&igrave; gi&aacute;o &aacute;n cutting, giảm c&acirc;n, giảm mỡ n&agrave;o. Đối với những gi&aacute;o &aacute;n tăng c&acirc;n, bulking th&igrave; bạn n&ecirc;n tập b&agrave;i n&agrave;y trước buổi tập để khởi động hoặc sau buổi tập nhằm mục đ&iacute;ch căng cơ phục hồi. V&agrave; cũng đừng qu&ecirc;n xoay v&ograve;ng c&aacute;c b&agrave;i tập thể h&igrave;nh kh&aacute;c nhau để mang đến cho cơ bắp 1 sự bối rối nhất định nh&eacute;.</div>', '3', '30', '30', null, null, null, null, null, null);
INSERT INTO exercisedetail VALUES ('12', '7', 'Straight-Arm Dumbbell Pullover – Vớt tạ', 'https://firebasestorage.googleapis.com/v0/b/fitneasy-d43a0.appspot.com/o/ANHGYM%2FBAITAPNGUC%2Fbai-tap-nguc-Dumbbell-Pullover.gif?alt=media&token=bada242a-7ab2-413e-8e50-db5d2e8b39e4', 'B&agrave;i n&agrave;y tập cho<strong><a href=\\\"http://www.thehinh.com/tag/bai-tap-nguc\\\" target=\\\"_blank\\\"> cơ ngực</a></strong>, <strong><a href=\\\"http://www.thehinh.com/tag/co-xo\\\" target=\\\"_blank\\\">cơ x&ocirc;</a></strong> v&agrave; c&aacute;c cơ kh&aacute;c như vai v&agrave; <strong><a href=\\\"http://www.thehinh.com/tag/co-tay-sau\\\" target=\\\"_blank\\\">cơ tay sau</a></strong>.<br />1. Nằm tr&ecirc;n ghế bằng, ch&acirc;n đặt tr&ecirc;n s&agrave;n, tạ để tr&ecirc;n đ&ugrave;i v&agrave; đưa tạ l&ecirc;n. Ch&uacute; &yacute; cần chắc tạ để kh&ocirc;ng bị rơi khi tập.<br />2. H&iacute;t v&agrave;o v&agrave; từ từ đưa tạ l&ecirc;n tr&ecirc;n ra ph&iacute;a sau đầu sao cho cơ ngực căng ra.<br />3. Thở ra v&agrave; đưa tạ l&ecirc;n tr&ecirc;n<br />Tập trong 1-2 hiệp<br />B&agrave;i n&agrave;y c&oacute; kh&aacute; nhiều biến thể như l&agrave; nằm tr&ecirc;n ghế, b&oacute;ng, hoặc ngửa ra sau&hellip;', '3', '30', '30', null, null, null, null, null, null);
INSERT INTO exercisedetail VALUES ('13', '8', 'Barbell Bench Press – Tập khe ngực.', 'https://firebasestorage.googleapis.com/v0/b/fitneasy-d43a0.appspot.com/o/ANHGYM%2FBAITAPNGUC%2Fbai-tap-nguc-Barbell-Bench-Press.gif?alt=media&token=b647b0a9-d464-4254-b636-42c765978374', '<div class=\\\"separator\\\"><strong>TH&Ocirc;NG TIN B&Agrave;I TẬP:</strong></div>\\n<div class=\\\"separator\\\"> </div>\\n<div class=\\\"separator\\\"><strong>Kiểu tập: </strong>                       Strength - Sức mạnh </div>\\n<div class=\\\"separator\\\"><strong>Nh&oacute;m cơ ch&iacute;nh:     </strong>        Chest - Cơ ngực</div>\\n<div class=\\\"separator\\\"><strong>Nh&oacute;m cơ tham gia:  </strong>      Shoulders - Cơ vai , Triceps - Cơ tay sau</div>\\n<div class=\\\"separator\\\"><strong>Loại t&aacute;c động:    </strong>            Compound </div>\\n<div class=\\\"separator\\\"><strong>Dụng cụ tập:     </strong>             Barbell - Thanh tạ</div>\\n<div class=\\\"separator\\\">Cấp độ:                           Basic - Cơ bản</div>\\n<div class=\\\"separator\\\"> </div>\\n<div class=\\\"separator\\\"><strong>HƯỚNG DẪN TẬP:</strong></div>\\n<div class=\\\"separator\\\"> </div>\\n<div class=\\\"separator\\\"> </div>\\n<div class=\\\"separator\\\"> </div>\\n<p><span id=\\\"result_box\\\" class=\\\"\\\" lang=\\\"vi\\\"><span class=\\\"hps\\\">1. Nằm l&ecirc;n ghế phẳng, 2 tay nắm v&agrave;o thanh tạ với độ rộng sao cho khi xuống tạ, c&aacute;nh tay song song với mặt đất th&igrave; c&aacute;nh tay tạo với cẳng tay 1 g&oacute;c 90 độ. N&acirc;ng thanh tạ ra khỏi gi&aacute;</span></span>. Giữ tay thẳng. Đ&acirc;y l&agrave; tư thế bắt đầu b&agrave;i tập.</p>\\n<div class=\\\"separator\\\"> </div>\\n<div class=\\\"separator\\\"><span id=\\\"result_box\\\" class=\\\"\\\" lang=\\\"vi\\\">3. Từ từ hạ tạ xuống ph&iacute;a dưới cho đến khi gần chạm v&agrave;o ngực giữa của bạn. H&iacute;t v&agrave;o trong suốt qu&aacute; tr&igrave;nh hạ tạ.     </span></div>\\n<div class=\\\"separator\\\"> </div>\\n<p><span id=\\\"result_box\\\" class=\\\"\\\" lang=\\\"vi\\\"><span class=\\\"hps\\\">4. </span></span>D&ugrave;ng cơ ngực đẩy thanh tạ l&ecirc;n. Thở ra. H&atilde;y nhớ, tập trung tinh thần, sự ch&uacute; &yacute; v&agrave;o cơ ngực, kh&ocirc;ng sử dụng qu&aacute; nhiều tay sau v&agrave; vai trong động t&aacute;c đẩy (Xem lại kỹ thuật ở ảnh hướng dẫn tr&ecirc;n tập lại cho ch&iacute;nh x&aacute;c)<br /><span id=\\\"result_box\\\" class=\\\"\\\" lang=\\\"vi\\\">  <br />5. Lặp lại chuyển động cho reps sau.</span><br /><br /></p>\\n<div class=\\\"separator\\\"><strong>TIPS:</strong></div>\\n<div class=\\\"separator\\\"> </div>\\n<div class=\\\"separator\\\"> </div>\\n<div class=\\\"separator\\\">1. Để đảm bảo b&agrave;i đẩy ngực kh&ocirc;ng ăn qu&aacute; nhiều v&agrave;o vai v&agrave; tay sau. bạn h&atilde;y thực hiện tập dượt động t&aacute;c trước khi bắt đầu đẩy. Lưu ảnh v&agrave;o trong điện thoại, ngồi trước gương, xoay người song song với gương. Tạo tư thế đ&uacute;ng v&agrave; sai, thử cử động xem sự kh&aacute;c nhau giữa 2 tư thế để c&oacute; cảm nhận tốt nhất.</div>\\n<div class=\\\"separator\\\"> </div>\\n<div class=\\\"separator\\\">2. Giữ vững sự tập trung tinh thần, kh&ocirc;ng để tay run, tạ lệch hoặc l&uacute;c lắc.</div>\\n<div class=\\\"separator\\\"> </div>\\n<div class=\\\"separator\\\">3. Hạ tạ c&oacute; điều khiển, kh&ocirc;ng để tạ rơi tự do.</div>\\n<div class=\\\"separator\\\"> </div>\\n<div class=\\\"separator\\\">Nguồn Gymlord, Thehinhonline.com</div>', '3', '30', '30', null, null, null, null, null, null);
INSERT INTO exercisedetail VALUES ('14', '9', 'Khởi động ver2', 'https://firebasestorage.googleapis.com/v0/b/fitneasy-d43a0.appspot.com/o/ANHGYM%2FBAITAPNGUC%2Fkhoi-dong-truoc-khi-tap-gym.png?alt=media&token=b763a3f1-c3b7-4cbb-811d-d7f3a47f2e33', 'Điều quan trọng nhất trong danh sách lịch tập gym 6 buổi của chúng ta là phải khởi động trước trong tất cả các buổi tập. Nếu bạn đã đọc bài viết những điều nên làm trước và sau khi tập bên trên thì chắc biết là khởi động quan trọng như thế nào rồi. Các bài khởi động như xoay co tay, chân, lưng, hông và chạy tại chỗ từ 2-5 phút. Có thể khỏi động thêm với tạ tay để làm nóng các cơ.', '3', '30', '30', null, null, null, null, null, null);
INSERT INTO exercisedetail VALUES ('15', '10', 'Back Squat – Gánh tạ', 'https://firebasestorage.googleapis.com/v0/b/fitneasy-d43a0.appspot.com/o/ANHGYM%2FCHAN_6%20BUOI%2Fbai-tap-mong-dui-Back-Squat%202.gif?alt=media&token=2c579d84-e249-4f6a-bf61-91c564e863a7', 'B&agrave;i n&agrave;y cũng kh&aacute; l&agrave; quen thuộc với c&aacute;c bạn Gymer n&ecirc;n ch&uacute;ng ta sẽ cho v&agrave;o lịch tập gym 6 buổi n&agrave;y, tuy nhi&ecirc;n nếu với người mới tập th&igrave; ch&uacute; &yacute; việc <strong><a href=\\\"http://www.thehinh.com/2016/01/khoa-khop-khi-tap-gym.html\\\" target=\\\"_blank\\\">kh&oacute;a khớp</a> nha</strong>. Kh&ocirc;ng g&atilde;y ch&acirc;n l&agrave; chuyện c&oacute; thể xảy ra đ&oacute;. B&agrave;i n&agrave;y tập v&agrave;o cơ đ&ugrave;i trước (Squad), nhưng với c&aacute;c <a href=\\\"https://www.facebook.com/TheHinhcom/posts/1850951475131427\\\" target=\\\"_blank\\\">vị tr&iacute; đặt ch&acirc;n kh&aacute;c th&igrave; sẽ tập v&agrave;o c&aacute;c nh&oacute;m kh&aacute;c</a>.</p> <p>1. Ngồi v&agrave;o m&aacute;y,đặt ch&acirc;n v&agrave;o gi&aacute; đỡ ch&acirc;n, ch&acirc;n rộng trung b&igrave;nh.<br />2. Mở thanh an to&agrave;n v&agrave; đẩy ch&acirc;n tới khi duỗi thẳng (kh&ocirc;ng thẳng ho&agrave;n to&agrave;n), th&acirc;n v&agrave; ch&acirc;n tạo th&agrave;nh 1 g&oacute;c 90 độ.<br />3. H&iacute;t v&agrave;o v&agrave; tự từ hạ gi&aacute; để ch&acirc;n xuống tới khi ch&acirc;n tr&ecirc;n v&agrave; dưới tạo th&agrave;nh 1 g&oacute;c 90 độ.<br />4. D&ugrave;ng g&oacute;t v&agrave; cơ đ&ugrave;i trước để đẩy gi&aacute; đặt ch&acirc;n trở lại vị tr&iacute; cũ v&agrave; thở ra.<br />Thực hiện trong 2 hiệp từ 10-12 lần', '3', '30', '30', null, null, null, null, null, null);
INSERT INTO exercisedetail VALUES ('16', '11', 'Legs Press', 'https://firebasestorage.googleapis.com/v0/b/fitneasy-d43a0.appspot.com/o/ANHGYM%2FCHAN_6%20BUOI%2Fbai-tap-chan-leg-press-1%203.1.gif?alt=media&token=eff8dd25-bc7e-4a9a-baf1-e9270c56e607', 'B&agrave;i n&agrave;y cũng kh&aacute; l&agrave; quen thuộc với c&aacute;c bạn Gymer n&ecirc;n ch&uacute;ng ta sẽ cho v&agrave;o lịch tập gym 6 buổi n&agrave;y, tuy nhi&ecirc;n nếu với người mới tập th&igrave; ch&uacute; &yacute; việc <strong><a href=\\\"http://www.thehinh.com/2016/01/khoa-khop-khi-tap-gym.html\\\" target=\\\"_blank\\\">kh&oacute;a khớp</a> nha</strong>. Kh&ocirc;ng g&atilde;y ch&acirc;n l&agrave; chuyện c&oacute; thể xảy ra đ&oacute;. B&agrave;i n&agrave;y tập v&agrave;o cơ đ&ugrave;i trước (Squad), nhưng với c&aacute;c <a href=\\\"https://www.facebook.com/TheHinhcom/posts/1850951475131427\\\" target=\\\"_blank\\\">vị tr&iacute; đặt ch&acirc;n kh&aacute;c th&igrave; sẽ tập v&agrave;o c&aacute;c nh&oacute;m kh&aacute;c</a>.</p> <p>1. Ngồi v&agrave;o m&aacute;y,đặt ch&acirc;n v&agrave;o gi&aacute; đỡ ch&acirc;n, ch&acirc;n rộng trung b&igrave;nh.<br />2. Mở thanh an to&agrave;n v&agrave; đẩy ch&acirc;n tới khi duỗi thẳng (kh&ocirc;ng thẳng ho&agrave;n to&agrave;n), th&acirc;n v&agrave; ch&acirc;n tạo th&agrave;nh 1 g&oacute;c 90 độ.<br />3. H&iacute;t v&agrave;o v&agrave; tự từ hạ gi&aacute; để ch&acirc;n xuống tới khi ch&acirc;n tr&ecirc;n v&agrave; dưới tạo th&agrave;nh 1 g&oacute;c 90 độ.<br />4. D&ugrave;ng g&oacute;t v&agrave; cơ đ&ugrave;i trước để đẩy gi&aacute; đặt ch&acirc;n trở lại vị tr&iacute; cũ v&agrave; thở ra.<br />Thực hiện trong 2 hiệp từ 10-12 lần.', '3', '30', '30', null, null, null, null, null, null);

